unsigned int toyHash(const unsigned char input[], unsigned int legnth){
    unsigned int tmp = 0xEF123ABC;
    for(unsigned int i=0;i<legnth;i++){
        tmp = (tmp << 5) + tmp + input[i];
    }
    return tmp;
}
void encrypt(unsigned char io[], unsigned int length, const unsigned int s[]){
    static unsigned char block[4][4];
    if(length % 16){
        for(unsigned int i=length;i<(length/16+1)*16;i++)
            io[i] = 0;
        length = (length / 16 + 1) * 16;
    }
    unsigned int hash = toyHash(io, length);
    *(unsigned int *)(io + length) = hash;
    for(unsigned int i=0;i<length;i++){
        hash ^= hash << 13;
        hash ^= hash >> 17;
        hash ^= hash << 5;
        io[i] ^= (unsigned char)hash;
    }
    length += 16;

    for(unsigned int i=0;i<length/16;i++){
        for(unsigned int j=0;j<4;j++)
            *(unsigned int *)block[j] = *(unsigned int *)(io + 16 * i + j * 4);
        singleEncrypt(block, s);
        for(unsigned int j=0;j<4;j++)
            *(unsigned int *)(io + 16 * i + j * 4) = *(unsigned int *)block[j];
    }
}
unsigned int decrypt(unsigned char io[], unsigned int length, const unsigned int s[]){
    static unsigned char block[4][4];
    if(length % 16){
        for(unsigned int i=length;i<(length/16+1)*16;i++)
            io[i] = 0;
        length = (length / 16 + 1) * 16;
    }
    for(unsigned int i=0;i<length/16;i++){
        for(unsigned int j=0;j<4;j++)
            *(unsigned int *)block[j] = *(unsigned int *)(io + 16 * i + j * 4);
        singleDecrypt(block, s);
        for(unsigned int j=0;j<4;j++)
            *(unsigned int *)(io + 16 * i + j * 4) = *(unsigned int *)block[j];
    }
    unsigned int hash = *(unsigned int *)(io + length - 16);
    for(unsigned int i=0;i<length-16;i++){
        hash ^= hash << 13;
        hash ^= hash >> 17;
        hash ^= hash << 5;
        io[i] ^= (unsigned char)hash;
    }
    hash = *(unsigned int *)(io + length - 16);
    *(unsigned int *)(io + length - 16) = 0;
    return hash;
}
