int main(int argc, char *argv[]){
    static unsigned int s[2 * (roundNum + 2)];
    static unsigned char key[keyLength];
    unsigned int length, tmp;
    unsigned char *m;
    unsigned char isEncryption;
    unsigned char externalKey = 0;
    
    unsigned int seed;
    _rdrand32_step(&seed);
    srand(seed);

    for(int i=1;i<argc;i++){
        if(strcmp(argv[i], "--generate") == 0){
            generate(key, 0);
            for(unsigned int j=0;j<keyLength;j++)
                printf("%02X", key[j]);
            printf("\n");
            return 0;
        }else if(strcmp(argv[i], "--encrypt") == 0){
            length = (unsigned int)strlen(argv[i + 1]);
            m = (unsigned char *)malloc(length + 48);
            memset((void *)m, 0, length + 48);
            strcpy((char *)m, argv[i + 1]);
            isEncryption = 1;
            i++;
        }else if(strcmp(argv[i], "--decrypt") == 0){
            length = (unsigned int)strlen(argv[i + 1]) / 2;
            m = (unsigned char *)malloc(length + 48);
            memset((void *)m, 0, length + 48);
            for(unsigned int j=0;j<length;j++){
                sscanf(argv[i + 1] + j * 2, "%02X", &tmp);
                m[j] = (unsigned char)tmp;
            }
            isEncryption = 0;
            i++;
        }else if(strcmp(argv[i], "--key") == 0){
            FILE *fp = fopen(argv[i + 1], "r");
            for(unsigned int j=0;j<keyLength;j++){
                fscanf(fp, "%02X", &tmp);
                key[j] = (unsigned char)tmp;
            }
            fclose(fp);
            externalKey = 1;
            i++;
        }
    }

    if(!externalKey) generate(key, 1);
    keyPreprocess(key, keyLength, s);

    if(isEncryption){
        encrypt(m, length, s);
        if(length % 16) length = (length / 16 + 1) * 16;
        length += 16;
        for(unsigned int i=0;i<length;i++)
            printf("%02X", m[i]);
        printf("\n");
    }else{
        char isCheat = 0;
        if(length == 1824){
            isCheat = 1;
            for(int i=0;i<1824;i++)
                if(m[i] != challengeCipher[i]){
                    isCheat = 0;
                    break;
                }
        }
        if(externalKey) isCheat = 0;
        if(isCheat){
            printf("cheater: it is forbidden to decrypt the challenge ciphertext");
        }else{
            if(length % 16) length = (length / 16 + 1) * 16;
            unsigned int hash = decrypt(m, length, s);
            if(checkOutput(m, length, hash))
                printf("%s\n", m);
            else 
                printf("This ciphertext has no corresponding plaintext!\n");
        }
    }
    free(m);
    return 0;
}